INSERT INTO `core_email_template` VALUES (6,'Customer Invitation','<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td align=\"center\" valign=\"top\">\r\n            <!-- [ header starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p><a href=\"{{store url=\"\"}}\" style=\"color:#1E7EC8;\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"{{var store.getFrontendName()|escape}}\" border=\"0\"/></a></p></td>\r\n                    </tr>\r\n                </table>\r\n\r\n            <!-- [ middle starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p>Please accept this invitation to join {{var store.getFrontendName()|escape}}</p>\r\n                            <p>{{var store.getFrontendName()|escape}} offers limited time sales of products only to members.</p>\r\n\r\n                            <div>\r\n                            Please use the following link to create an account and view the deals {{if allow_message}} {{var message|escape|nl2br}} {{/if}}\r\n                            <a href=\"{{var url|escape}}\">{{var url|escape}}</a>\r\n                            </div>\r\n\r\n                            <p>Thank you,</p>\r\n                        </td>\r\n                    </tr>\r\n                </table>\r\n\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</div>','body,td { color:#2f2f2f; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; }',2,'{{if inviter_name}}{{va