INSERT INTO `core_email_template` VALUES (20,'New Account','<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td align=\"center\" valign=\"top\">\r\n                <!-- [ header starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <a href=\"{{store url=\"\"}}\"><img src=\"{{skin url=\"images/logo_email.gif\" _area=\'frontend\'}}\" alt=\"{{var store.getFrontendName()}}\"  style=\"margin-bottom:10px;\" border=\"0\"/></a></td>\r\n                    </tr>\r\n                </table>\r\n\r\n                <!-- [ middle starts here] -->\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p><strong>Dear {{htmlescape var=$customer.name}}</strong>,<br/>\r\n                            Welcome to {{var store.getFrontendName()}}. To log in when visiting our site just click <a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">Login</a> or <a href=\"{{store url=\"customer/account/\"}}\" style=\"color:#1E7EC8;\">My Account</a> at the top of every page, and then enter your e-mail address and password.</p>\r\n\r\n                            <p style=\"border:1px solid #BEBCB7; padding:13px 18px; background:#F8F7F5; \">\r\n                                Use the following values when prompted to log in:<br/>\r\n                                E-mail: {{var customer.email}}<br/>\r\n                                Password: {{htmlescape var=$customer.password}}<p>\r\n\r\n                            <p>When you log in to your account,