INSERT INTO `core_email_template` VALUES (34,'Reward Points Balance Update','<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td>\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p>Hello, {{var customer.getName()|escape}}.</p>\r\n\r\n                            <p>You have {{var points_balance}} pts that can be used in our store <a href=\"{{store url=\"\"}}\">{{var store.getFrontendName()|escape}}</a>.</p>\r\n\r\n                            <p>If you have any questions about your account or any other matter, please feel free to contact us\r\n                            at <a href=\"mailto:{{config path=\'trans_email/ident_sales/email\'}}\">{{config path=\'trans_email/ident_sales/email\'}}</a>\r\n                            or by phone at {{config path=\'general/store_information/phone\'}}.</p>\r\n\r\n                            <p>You can <a href=\"{{var unsubscription_url}}\">unsubscribe</a> from such notifications.</p>\r\n                        </td>\r\n                    </tr>\r\n                </table>\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</div>','body,td { color:#2f2f2f; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; }',2,'Reward Points Balance Updated at {{var store.getFrontendName()|escape}}',NULL,NULL,'2010-04-06 14:46:02','2010-04-06 14:46:02','enterprise_reward_notification_balance_update_template','{\"store url=\\\"\\\"\":\"Store Url\",\"skin url=\\\"images/logo_email.gif\\\" _area=\'frontend\'\":\"Email Logo Image\",\"config path=\'trans_email/ident_sales/email\'\":\"Sales Contact Email\",\"config path=\'general/store_information/phone\'\":\"Sale