INSERT INTO `core_email_template` VALUES (35,'Reward Points Expiry Warning','<div style=\"font:11px/1.35em Verdana, Arial, Helvetica, sans-serif;\">\r\n    <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"98%\" style=\"margin-top:10px; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; margin-bottom:10px;\">\r\n        <tr>\r\n            <td>\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" width=\"650\">\r\n                    <tr>\r\n                        <td valign=\"top\">\r\n                            <p>Hello, {{var customer_name|escape}}.</p>\r\n\r\n                            <p>There are {{var remaining_days}} day(s) remaining to use your {{var points_expiring}} Reward Point(s).</p>\r\n                            <p>After {{var remaining_days}} day(s) this amount will be expired.</p>\r\n                            <p>You have {{var points_balance}} pts that can be used in our store <a href=\"{{store url=\"\"}}\">{{var store.getFrontendName()|escape}}</a>.</p>\r\n\r\n                            <p>If you have any questions about your account or any other matter, please feel free to contact us\r\n                            at <a href=\"mailto:{{config path=\'trans_email/ident_sales/email\'}}\">{{config path=\'trans_email/ident_sales/email\'}}</a></p>\r\n                            or by phone at {{config path=\'general/store_information/phone\'}}.\r\n\r\n                            <p>You can <a href=\"{{var unsubscription_url}}\">unsubscribe</a> from such notifications.</p>\r\n                        </td>\r\n                    </tr>\r\n                </table>\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</div>','body,td { color:#2f2f2f; font:11px/1.35em Verdana, Arial, Helvetica, sans-serif; }',2,'Reward Points Expiry Warning at {{var store.getFrontendName()|escape}}',NULL,NULL,'2010-04-06 14:46:30','2010-04-06 14:46:30','enterprise_reward_notification_expiry_warning_template','{\"store url=\\